function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading)
        return '';

	var ajax = new GlideAjax('x_58872_needit.NeedItAjax');
    ajax.addParam('sysparm_name', 'getUserEmail');
    ajax.addParam('sysparm_user_id', newValue);
    ajax.getXML(setEmail);
	console.log('newValue2 = ' + newValue);
    function setEmail(response) {
		console.log('response = ' + JSON.stringify(response));
		var email = response.responseXML.documentElement.getAttribute("answer");
		g_form.setValue('u_requested_for_email',email);
    }

}
