sys_script_include.script:
var NeedItAjax = Class.create();
NeedItAjax.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getUserEmail: function() {

        var userID = this.getParameter('sysparm_user_id');

		gs.info('NeedItAjax - userID = ' + userID);

        var userGr = new GlideRecord('sys_user');
        if (userGr.get(userID)) {
			gs.info('NeedItAjax - userID2 = ' + userGr.email);
            return userGr.email + '';
        }
		gs.info('NeedItAjax - userID3 = ');
        return '';
    },
    type: 'NeedItAjax'
});
