sys_script_include.script:
var NeedItUtil = Class.create();
NeedItUtil.prototype = {
    initialize: function() {
    },
	getName: function(firstName, lastName,state){
		if(state == 'TN'){
			return firstName + " " + lastName;
		}else{
			return lastName + " " + firstName;
		}
	},
	isDatePast:function(dateStr){
		var rightNow = new GlideDateTime();
		var inputDate = new GlideDateTime(dateStr);
		if (inputDate.before(rightNow)){
			return true;
		}
		return false;
	},
	isDateToday:function(dateStr){
		var rightNow = new GlideDateTime().getLocalDate();
		var inputDate = new GlideDateTime(dateStr).getLocalDate();

		if (inputDate.compareTo(rightNow) == 0){
			return true;
		}
		return false;
	},
    type: 'NeedItUtil'
};
